import React from 'react';
import { makeStyles } from '@material-ui/core/styles';
import ButtonBase from '@material-ui/core/ButtonBase';
import Typography from '@material-ui/core/Typography';
import { Link as RouterLink, Redirect, useHistory, useLocation } from 'react-router-dom';
import {ProtectedRoute} from '../Protect_Route';
import {Switch,Route} from 'react-router-dom';
import PieChart  from '../reports/pieChart'
import BarChart  from '../reports/barChart'
import LineChart  from '../reports/lineChart'
import DonutChart  from '../reports/donutChart'


const useStyles = makeStyles((theme) => ({
  root: {
    display: 'flex',
    flexWrap: 'wrap',
    minWidth: 300,
    width: '100%',
    marginLeft:100,
    flexDirection:"column"

  },
  image: {
    position: 'relative',
    height: 250,
    margin: 10,
    [theme.breakpoints.down('xs')]: {
      width: '100% !important', // Overrides inline-style
      height: 100,
      
    },
    '&:hover, &$focusVisible': {
      zIndex: 1,
      '& $imageBackdrop': {
        opacity: 0.15,
      },
      '& $imageMarked': {
        opacity: 0,
      },
      '& $imageTitle': {
        border: '4px solid currentColor',
      },
    },
  },
  focusVisible: {},
  imageButton: {
    position: 'absolute',
    left: 0,
    right: 0,
    top: 0,
    bottom: 0,
    display: 'flex',
    alignItems: 'center',
    justifyContent: 'center',
    color: theme.palette.common.white,
  },
  imageSrc: {
    position: 'absolute',
    left: 0,
    right: 0,
    top: 0,
    bottom: 0,
    backgroundSize: 'cover',
    backgroundPosition: 'center 40%',
  },
  imageBackdrop: {
    position: 'absolute',
    left: 0,
    right: 0,
    top: 0,
    bottom: 0,
    backgroundColor: theme.palette.common.black,
    opacity: 0.4,
    transition: theme.transitions.create('opacity'),
  },
  imageTitle: {
    position: 'relative',
    padding: `${theme.spacing(2)}px ${theme.spacing(4)}px ${theme.spacing(1) + 6}px`,
  },
  imageMarked: {
    height: 3,
    width: 18,
    backgroundColor: theme.palette.common.white,
    position: 'absolute',
    bottom: -2,
    left: 'calc(50% - 9px)',
    transition: theme.transitions.create('opacity'),
  },
  title:{
    fontSize:"50px",
    marginRight:theme.spacing(45),
    fontFamily:"cursive",
    // fontWeight:"bold",
  },
}));

export default function Reports() {
  const history=useHistory();
  const handleBarChart=()=>{
    history.push("/BarChart")
  }
  const handlePieChart=()=>{
    history.push("/PieChart")
  }
  const handleLineChart=()=>{
    history.push("/LineChart")
  }
  const handleDonutChart=()=>{
    history.push("/DonutChart")
  }
const images = [
  {
    url: 'https://media.istockphoto.com/vectors/bar-chart-flat-icon-pixel-perfect-for-mobile-and-web-vector-id1152899468?k=6&m=1152899468&s=612x612&w=0&h=vhpFMyUQzPgxPv0pL94npANkEsgVAhW_xmEXMel7AVw=',
    title: 'BarChart',
    width: '35%',
    onclick:handleBarChart,
    
  },
  {
    url: 'https://gracegritgratitude.co.nz/wp-content/uploads/2018/09/piechart-1024x576.jpg',
    title: 'PieChart',
    width: '45%',
    onclick:handlePieChart,
    
  },
  // {
  //   url: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcSMrKQoiFQH1oPsjBredsZABvz-t67MMTC5VFOIwql0E3lI0lxAeezZxyQ0GWDeLueu2b8&usqp=CAU',
  //   title: 'LineChart',
  //   width: '35%',
  //   onclick:handleLineChart,

  // },
  // {
  //   url: 'https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQ11VTBKwqYTDQo-jrxJjIuoaFZSSh6V5K-eg&usqp=CAU',
  //   title: 'DonutChart',
  //   width: '35%',
  //   onclick:handleDonutChart,

  // },
];

  const classes = useStyles();
  return (
    <div className={classes.root}>
      <div>
       <h1 align="center" className={classes.title}>Reports</h1>
       </div>
       <br/>
       <div>
      {images.map((image) => (
        <ButtonBase onClick={image.onclick}
          focusRipple
          key={image.title}
          className={classes.image}
          focusVisibleClassName={classes.focusVisible}
          style={{
            width: image.width,
          }}
        >
          <span
            className={classes.imageSrc}
            style={{
              backgroundImage: `url(${image.url})`,
            }}
          />
          <span className={classes.imageBackdrop} />
          <span className={classes.imageButton}>
            <Typography
              component="span"
              variant="subtitle1"
              color="inherit"
              className={classes.imageTitle}
            >
              {image.title}
              <span className={classes.imageMarked} />
            </Typography>
          </span>
        </ButtonBase>
        
      ))}
      </div>
      <Switch>
           <ProtectedRoute
            exact
            path="/PieChart"
            component={PieChart }
          />:
          <ProtectedRoute
            exact
            path="/BarChart"
            component={BarChart }
          />
          <ProtectedRoute 
           exact 
           path="/LineChart" 
           component={LineChart } />
          <ProtectedRoute
            exact
            path="/DonutChart"
            component={DonutChart}
          />
          
        </Switch>
    </div>
  );
}
